import { useState,useEffect } from "react";
import axios from "axios"
const Filter = ({filter,setFilter}) => {
  return(<div>
    Filter name with <input value = {filter} onChange = {(event) => {
        setFilter(event.target.value);
      }}/>
  </div>)
}

const PersonForm = ({persons,setPersons}) => {
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  return(
    <form
        onSubmit={(event) => {
          event.preventDefault();
          if (persons.map((a) => a.name).includes(newName))
            alert(`${newName} already exists in the Phonebook.`);
          else {
            const newPerson = { name: newName ,number: newNumber}
            axios
            .post("http://localhost:3001/persons",newPerson)
            .then(() => setPersons(persons.concat()))
          }
          setNewName("");
          setNewNumber("");
        }}
      >
        <div>
          name:{" "}
          <input
            value={newName}
            onChange={(event) => {
              setNewName(event.target.value);
            }}
          />
        </div>
        <div>
          number:{" "}
          <input
            value={newNumber}
            onChange={(event) => {
              setNewNumber(event.target.value);
            }}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
  )
}

const Persons = ({ persons }) => {
return(
  <div>
        {persons.map((person) => (
          <p key={person.name}>{person.name}: {person.number}</p>

        ))}
      </div>
)
}

const App = () => {
  const [persons, setPersons] = useState([]);
  const [filter,setFilter] = useState("");
  useEffect(() => {
    axios
    .get("http://localhost:3001/persons")
    .then((response) => {
      const arr = response.data;
      setPersons(arr)
    })
  },[])
  const displayedPersons = filter?persons.filter((person) => person.name.toLowerCase().includes(filter.toLowerCase())):persons
  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filter = {filter} setFilter = {setFilter}/>
      <h2>Add a New</h2>
      <PersonForm persons = {persons} setPersons = {setPersons}/>
      <h2>Numbers</h2>
      <Persons persons = {displayedPersons}/>
    </div>
  );
};

export default App;
