import { useState,useEffect } from "react";
import personService from "./services/persons.js"

const Filter = ({filter,setFilter}) => {
  return(<div>
    Filter name with <input value = {filter} onChange = {(event) => {
        setFilter(event.target.value);
      }}/>
  </div>)
}

const PersonForm = ({persons,setPersons}) => {
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  return(
    <form
        onSubmit={(event) => {
          event.preventDefault();
          if (persons.map((a) => a.name).includes(newName))
            alert(`${newName} already exists in the Phonebook.`);
          else {
            const newPerson = { name: newName ,number: newNumber}
            personService
            .create(newPerson)
            .then((newPerson) => {
              setPersons(persons.concat(newPerson))
              setNewName("");
              setNewNumber("");
            })
          }

        }}
      >
        <div>
          name:{" "}
          <input
            value={newName}
            onChange={(event) => {
              setNewName(event.target.value);
            }}
          />
        </div>
        <div>
          number:{" "}
          <input
            value={newNumber}
            onChange={(event) => {
              setNewNumber(event.target.value);
            }}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
  )
}

const Persons = ({ persons,deletePerson }) => {
return(
  <div>
    {persons.map((person) => (
      <div key = {person.id}>
      <p>{person.name}: {person.number}</p>
      <button onClick = {() => deletePerson(person.id)}>Delete</button>
      </div>
    ))}
  </div>
)
}

const App = () => {
  const [persons, setPersons] = useState([]);
  const [filter,setFilter] = useState("");
  useEffect(() => {
    personService
    .getAll()
    .then((persons) => {
      setPersons(persons)
    })
  },[])
  const displayedPersons = filter?persons.filter((person) => person.name.toLowerCase().includes(filter.toLowerCase())):persons
  const deletePerson = (id) => 
  personService
  .del(id)
  .then(deletedPerson => {
    console.log(deletedPerson)
    setPersons(persons.filter(person => person.id !== deletedPerson.id))
  })
  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filter = {filter} setFilter = {setFilter}/>
      <h2>Add a New</h2>
      <PersonForm persons = {persons} setPersons = {setPersons}/>
      <h2>Numbers</h2>
      <Persons persons = {displayedPersons} deletePerson = {deletePerson}/>
    </div>
  );
};

export default App;
